import React, {useState, useEffect} from 'react';
import { View, Text, StyleSheet, TextInput, Image, SafeAreaView, FlatList} from 'react-native';
import CustomInput from '../../components/customInput';
import CustomButton from '../../components/customButton';
import AsyncStorage from '@react-native-async-storage/async-storage';

const Search = () => {

  const getAllUsers = async () => {
    const value = await AsyncStorage.getItem('@session_token');
    return fetch("http://localhost:3333/api/1.0.0/search", {
          'headers': {
            'X-Authorization':  value
          }
        })
        .then((response) => {
            if(response.status === 200){
                return response.json()
            }else if(response.status === 401){
              this.props.navigation.navigate("Login");
            }else{
                throw 'Something went wrong';
            }
        })
        .then((responseJson) => {
          this.setState({
            isLoading: false,
            listData: responseJson
          })
        })
        .catch((error) => {
            console.log(error);
        })
  };

  const checkLoggedIn = async () => {
    const value = await AsyncStorage.getItem('@session_token');
    if (value == null) {
        this.props.navigation.navigate('Login');
    }
  };

   useEffect(() => {
        checkLoggedIn();
        getAllUsers();
    }, []);

    return (
		<SafeAreaView style={styles.root}>
            <View style={styles.header}></View>
                <View style={styles.container}>
                    {/*Search Page*/}
                    <View style={styles.titleContainer}>
                        <Text style={styles.sectionTitle}>Search</Text>
			        </View>
                    {/*Text Input for Searching for friends*/}
			        <CustomInput placeholder="Enter a name..."/>
			        {/*Button to Search*/}
			        <CustomButton text="Find Friends"/>
                    <View style={styles.bodyContent}>
                        <FlatList
                            data={this.state.listData}
                            renderItem={({item}) => (
                                <View>
                                    <Text style={styles.results}>{item.user_givenname} {item.user_familyname}</Text>
                                </View>
                            )}
                            keyExtractor={(item,index) => item.user_id.toString()}
                        />
                    </View>
                </View>
		</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	root: {
		flex: 1,
		backgroundColor: '#ffcfe6',
	},
	header:{
        backgroundColor: "#45ded0",
        height:200,
    },
	container: {
		flex: 1,
		justifyContent: 'flex-start',
		alignItems: 'center',
		padding: 30
	},
	titleContainer: {
		paddingTop: 80,
		paddingHorizontal: 40,
	},
	sectionTitle: {
		fontSize:28,
        color: "#696969",
        fontWeight: "600"
	},
    results: {
        fontSize:16,
        color: "#696969",
        marginTop:10
    }
});

export default Search;
