import React from 'react';
import { Text, View, SafeAreaView, StyleSheet } from 'react-native';
import CustomButton from '../../components/customButton';

const Logout = () => {

	const onLogoutPressed = () => {
		//logout button
		return fetch("http://localhost:3333/api/1.0.0/logout", {
			method: "POST",
		}).then((response) => {
			if (response.status === 201 || response.status === 200) {
				console.log("fetch successful!")
				return response.json();
			} else if (response.status === 400) {
				throw "Bad data - Logout unsuccessful"
			} else if (response.status === 401) {
				throw "Bad data - Logout unauthorised"
			} else {
				throw "Something went wrong: " + response.status
			}
		}).then((responseJson) => {
			navigation.navigate("Login");
		}).catch((err) => {
			console.log(err);
		})

	}
	return (
		<SafeAreaView style={styles.root}>
			<View style={styles.container}>
			{/*Spacebook Logo*/}
			{/*Login Title*/}
			<View style={styles.titleContainer}>
				<Text style={styles.sectionTitle}>Are you sure you want to Log out?</Text>
			</View>
			{/*Button to Click Logout*/}
			<CustomButton text="Logout" onPress={onLogoutPressed} />
			</View>
		</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	root: {
		flex: 1,
		backgroundColor: '#ffcfe6',
	},
	//logo: {
	//	width: 120,
	//	height: 120,
	//},
	container: {
		flex: 1,
		justifyContent: 'space-evenly',
		alignItems: 'center',
		padding: 10
	},
	titleContainer: {
		paddingTop: 80,
		paddingHorizontal: 40,
	},
	sectionTitle: {
		fontSize: 24,
		fontFamily: 'helvetica',
	}
});

export default Logout;
