import React, { Component } from 'react';
import { Text, View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createDrawerNavigator } from '@react-navigation/drawer';
import { Ionicons } from '@expo/vector-icons';

import Login from './screens/Login';
import Home from './screens/Home';
import Profile from './screens/Profile';
import Search from './screens/Search';
import AccountSettings from './screens/AccountSettings';
import Logout from './screens/Logout';


//----------------------------------------------
//App Main

function navigateHome({ navigation }) {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Button
        onPress={() => navigation.navigate('AccountSettings')}
        title="Go To Account Settings"
      />
    </View>
  );
}

function navigateAccountSettings({ navigation }) {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Button onPress={() => navigation.goBack()} title="Go back home" />
    </View>
  );
}

function navigateLogout({ navigation }) {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Button
        onPress={() => navigation.navigate('Logout')}
        title="Logout"
      />
    </View>
  );
}

const Drawer = createDrawerNavigator();
const Tab = createBottomTabNavigator();

export default function App() {
    return ( 
    //login function somewhere here to redirect to either sign up or home (if authenticated)
        <NavigationContainer>
            <Tab.Navigator //main page when logged in
                screenOptions={({ route }) => ({
                    tabBarIcon: ({ focused, color, size }) => {
                        let iconName;

                        if (route.name === 'Home') {
                            iconName = focused
                                ? 'ios-planet'
                                : 'ios-planet-outline';
                        } else if (route.name === 'Profile' ) {
                            iconName = focused
                                ? 'ios-person'
                                : 'ios-person-outline';
                        } else if (route.name === 'Search' ) {
                            iconName = focused
                                ? 'ios-search'
                                : 'ios-search-outline';
                        }

                        // You can return any component that you like here!
                        return <Ionicons name={iconName} size={size} color={color} />;
                    },
                    tabBarActiveTintColor: '#45ded0',
                    tabBarInactiveTintColor: '#28a3a5',
                })}
            >
                <Tab.Screen name="Home" component={Home} options={{ headerShown: false }} />
                <Tab.Screen name="Profile" component={Profile} options={{ headerShown: false }}/>
                <Tab.Screen name="Search" component={Search} options={{ headerShown: false }}/>

                <NavigationContainer>
                    <Drawer.Navigator initialRouteName="Home">
                        <Drawer.Screen name="AccountSettings" component={AccountSettings} />
                        <Drawer.Screen name="Logout" component={Logout} />
                    </Drawer.Navigator>
                </NavigationContainer>
            </Tab.Navigator>
        </NavigationContainer>
    );
}
