import React, { Component } from 'react';
import { useState, useEffect } from 'react';
import { Text, View, SafeAreaView, StyleSheet, ActivityIndicator, FlatList, Button, Alert, AsyncStorage } from 'react-native';
import CustomInput from '../../components/customInput';
import CustomButton from '../../components/customButton';


const Signup = ({ navigation }) => {
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [first_name, setFirstName] = useState('');
	const [last_name, setLastName] = useState('');
	const [passwordCheck, setPasswordCheck] = useState('');

	const save = async () => {
		try {
			await AsyncStorage.setItem("name", first_name);
			console.log(first_name);
		} catch (err) {
			alert(err);
        }
	}
	const load = async () => {
		try {
			let first_name = await AsyncStorage.getItem("name");
			if (first_name !== null) {
				setFirstName(first_name);
            }
		} catch (err) {
			alert(err);
		}
	}
	useEffect(() => {
		load()
	}, [])

	const onSignUpPressed = () => {
		//if all forms completed in sign up
		//check not already existing on server (GET data)
		//POST sign up details store in async storage
		//console.log("mounted");
		//getData();
		//addItem();
		console.warn("User: " + email);
		console.warn("Password: " + password);
		navigation.navigate("Home");

	}

	const onReturnPressed = () => {
		console.warn("Returned!");
		navigation.navigate("Login");
	}
	return (
			<SafeAreaView style={styles.root}>
				<View style={styles.container}>
				{/*Spacebook Logo*/}
				{/*Sign Up Title*/}
				<View style={styles.titleContainer}>
					<Text style={styles.sectionTitle}>Create Account</Text>

				</View>

				{/*Text Input for Creating an Account*/}
				<CustomInput placeholder="First Name" value={first_name} setValue={setFirstName} />
				<CustomInput placeholder="Last Name" value={last_name} setValue={setLastName} />
				<CustomInput placeholder="Email Address" value={email} setValue={setEmail} />
				<CustomInput secureTextEntry={true} placeholder="Password" value={password} setValue={setPassword} />
				<CustomInput secureTextEntry={true} placeholder="Confirm Password" value={passwordCheck} setValue={setPasswordCheck} />


				{/*Button to Sign Up*/}
				<CustomButton text="Create Account" onPress={() => save()} />

				{/*Button to Return*/}
				<CustomButton text="Back to Login" onPress={onReturnPressed} />

				</View>
			</SafeAreaView>
	);


	const getData = () => {
		console.log("getting data...");
		return fetch("http://localhost:3333/list")
			.then((response) => response.json())
			.then((responseJson) => {
				console.log(responseJson);
				this.setState({
					isLoading: false,
					shoppingListData: responseJson
				})
			})
			.catch((error) => {
				console.log(error);
			});
		console.log("data got");
	}

	const addItem = () => {
		let to_send = {
			id: parseInt(this.state.id),
			first_name: this.state.first_name,
			last_name: this.state.last_name,
			email: this.state.email,
			password: this.state.password
		};

		return fetch("http://localhost:3333/list", {
			method: 'post',
			headers: {
				'Content-Type': 'application/json'
			},
			body: JSON.stringify(to_send)
		})
			.then((response) => {
				Alert.alert("Item added");
				this.getData();
			})
			.catch((error) => {
				console.log(error);
			})
	}
}






const styles = StyleSheet.create({
	root: {
		flex: 1,
		backgroundColor: '#ffcfe6',
	},
	//logo: {
	//	width: 120,
	//	height: 120,
	//},
	container: {
		flex: 1,
		justifyContent: 'space-evenly',
		alignItems: 'center',
		padding: 10,
	},
	titleContainer: {
		paddingTop: 80,
		paddingHorizontal: 40,
	},
	sectionTitle: {
		fontSize: 24,
		fontFamily: 'helvetica',
	}
});

export default Signup;
