import React, {useState, useEffect} from 'react';
import { View, Text, StyleSheet, TextInput, Image, SafeAreaView, FlatList, ScrollView} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import CustomButtonSmall from '../../components/customButtonSmall';

const FriendRequests = ({ navigation }) => {
	const [friendRequests, setFriendRequests] = useState('');
    const [errorMess, setErrorMess] = useState('0 new requests.');

    useEffect(() => {
        navigation.addListener('focus', () => {
            refreshPage();
        })
        checkLoggedIn();
        getFriendRequests();
    }, []);

    const refreshPage = () => {
        checkLoggedIn();
        getFriendRequests();
    }

	const getFriendRequests = async () => {
        const value = await AsyncStorage.getItem('@session_token');
        const userID = await AsyncStorage.getItem('@user_id')
        return fetch("http://localhost:3333/api/1.0.0/friendrequests", {
            headers: {
            'X-Authorization':  value
            }
        })
        .then((response) => {
            if(response.status === 200){
                return response.json()
            }else if(response.status === 401){
              this.props.navigation.navigate("Login");
            }else{
                throw 'Something went wrong';
            }
        }).then((responseJson) => {
            setFriendRequests(responseJson);
            if(responseJson.length !== 0) {
                setErrorMess('');
            }
        })
        .catch((error) => {
            console.log(error);
        })
    }

    const onAccept = async (requestedUserID) => {
        const value = await AsyncStorage.getItem('@session_token');
        return fetch("http://localhost:3333/api/1.0.0/friendrequests/"+requestedUserID, {
            method: "POST",
            headers: {
            'X-Authorization':  value
            }
        }).then((response) => {
            //console.log(response.status);
            if(response.status === 200 || response.status === 201){
                setErrorMess('Request Accepted.');
                return response.json()
            }else if(response.status === 401){
              navigation.navigate("Login");
            }else{
                throw 'Something went wrong';
            }
        }).then((responseJson) => {
            console.log("Request Accepted.");
        })
        .catch((error) => {
            console.warn(error);
        })
    }

    const onReject = async (requestedUserID) => {
        const value = await AsyncStorage.getItem('@session_token');
        return fetch("http://localhost:3333/api/1.0.0/friendrequests/"+requestedUserID, {
            method: "DELETE",
            headers: {
            'X-Authorization':  value
            }
        }).then((response) => {
            console.log(response.status);
            if(response.status === 200 || response.status === 201){
                setErrorMess('Request Rejected.');
                return response.json()
            }else if(response.status === 401){
              navigation.navigate("Login");
            }else{
                throw 'Something went wrong';
            }
        }).then((responseJson) => {
            console.log("Request Rejected.");
            
        })
        .catch((error) => {
            console.warn(error);
        })
    }
    
    const checkLoggedIn = async () => {
        const value = await AsyncStorage.getItem('@session_token');
        if (value == null) {
            navigation.navigate('Login');
        }
    };

    const ItemView = ({item}) => {
        if (friendRequests.length === 0){
            console.log("0 New Requests.");
            return (
                <View>
                    <Text style={styles.text}>0 New Requests.</Text>
                </View>
            );
        } else {
            return (
                <View>
                    <Text style={styles.text}>
                        {item.id}{item.first_name}{' '}{item.last_name}{' '}<CustomButtonSmall text="Accept" onPress={() => onAccept(item.user_id)}/>{' '}<CustomButtonSmall text="Reject" onPress={() => onReject(item.user_id)}/>
                    </Text>
                </View>
            );
        } 
    }

    const ItemSeperatorView = () => {
        return (
            <View 
                style={{height: 0.5, width: '100%', backgroundColor: 'white'}}
            />
        );
    }

	return (
	<SafeAreaView style={styles.root}>
        <ScrollView>
            <View style={styles.header}>
                {/*Friends Requests Page*/}
                <View style={styles.titleContainer}>
                    <Text style={styles.sectionTitle}>Friend Requests</Text>
    	        </View>
            </View>
            <View style={styles.container}>
                <Text style={styles.text}>{errorMess}</Text>
                <View style={styles.text}>
                    <FlatList
                        data={friendRequests}
                        keyExtractor={(item, index) => index.toString()}
                        ItemSeperatorComponent={ItemSeperatorView}
                        renderItem={ItemView}
                    />
                </View>  
            </View>
        </ScrollView>
	</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	root: {
		flex: 1,
		backgroundColor: '#ffcfe6',
	},
	header:{
        backgroundColor: "#45ded0",
        height: 200,
        padding: 10,
    },
	container: {
		flex: 1,
		justifyContent: 'flex-start',
		alignItems: 'center',
		padding: 30
	},
	titleContainer: {
		paddingTop: 80,
		paddingHorizontal: 40,
        alignItems: 'center',
	},
	sectionTitle: {
		fontSize:28,
        color: "#696969",
        fontWeight: "600"
	},
    text: {
        fontSize:16,
        color: "#696969",
        marginTop:10,
        fontWeight: 'bold'
    },
    inputContainer: {
		backgroundColor: '#fff',
		width: '90%',
		borderRadius: 60,
		borderColor: '#45ded0',
		borderWidth: 2,	
        paddingHorizontal: 20,
		paddingVertical: 10,
		borderRadius: 60,
    }
});

export default FriendRequests;
